// Print Demo
package METHOD3;
public class Method09 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String S ="Mayur";
		System.out.printf("% S ",S);
		System.out.printf("\n%S,S");
		System.out.printf("\n%20 S",S);
		System.out.printf("\n%-20 S",S );    //Left justified

		float a =12.34456789f;
		System.out.printf("\n%f",a	);
		System.out.printf("\n%10.2f",a);
		System.out.printf("\n%-10.2f",a);
		System.out.printf("\n%-4.2f",a);
	}
}
